INSERT INTO public.appeal (id, text, detailed_text, confirmed_theme) VALUES (8521, 'Отсутствует столбовое освещение . есть столбы и провода , ОКЭС приехали сняли светильники и уехали , через наш двор проходит дорога в школу и 3 детских сада. Скоро будет рано темнеть и детям будет тяжело ходить. Писала обращение в администрацию города и УЖКХ приехали ОКЭС подключили 1 столб на углу МКД и уехали. верните наши фонари.', null, 4);
